import {Component, OnInit, TemplateRef} from '@angular/core';

import {take} from 'rxjs/operators';
import {NgbOffcanvas} from '@ng-bootstrap/ng-bootstrap';

import {Store} from '@ngrx/store';
import {takeUntilDestroyed} from '@angular/core/rxjs-interop';
import {LanguageDto, UserDto} from '../generated';
import {AppState} from './shared/state/app.state';
import {selectUser} from './shared/state/data/data.selectors';
import {JwtAuthService} from './auth/jwt-auth.service';
import {selectIsLoading} from './shared/state/visual/visual.selectors';

export type SupportedLanguage = 'de' | 'fr' | 'it' | 'en';
export const supportedLanguages: SupportedLanguage[] = ['de', 'fr', 'it', 'en'];

export const supportedLanguageToLanguageDto: Record<SupportedLanguage, LanguageDto> = {
  de: LanguageDto.De,
  fr: LanguageDto.Fr,
  it: LanguageDto.It,
  en: LanguageDto.En
};

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  companyLogo =
    'data:image/jpeg;base64,';
  user: UserDto | undefined;
  isLoading: boolean = false;

  title = 'hackathon-ui';

  page = '/';

  constructor(
    private store: Store<AppState>,
    private jwtAuthService: JwtAuthService,
    private offCanvasService: NgbOffcanvas
  ) {
    this.store
      .select(selectUser)
      .pipe(takeUntilDestroyed())
      .subscribe((user) => (this.user = user));

    this.store
      .select(selectIsLoading)
      .pipe(takeUntilDestroyed())
      .subscribe((isLoading) => (this.isLoading = isLoading || false));
  }

  ngOnInit(): void {
    // If the auth service is not initialized, run the initial login sequence.
    this.jwtAuthService.isInitialized$.pipe(take(1)).subscribe((isInitialized) => {
      if (!isInitialized) {
        this.jwtAuthService.loadUserAndFinishLoginSequence();
      }
    });
  }

  openMobileNav(content: TemplateRef<object>) {
    this.offCanvasService.open(content, {
      position: 'end',
      panelClass: 'offcanvas-mobile-nav'
    });
  }
}
